--
-- Структура таблицы `news`
--

DROP TABLE IF EXISTS `news`;
CREATE TABLE IF NOT EXISTS `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(150) COLLATE utf8_unicode_ci NOT NULL,
  `content` text COLLATE utf8_unicode_ci NOT NULL,
  `date` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `user_id` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Дамп данных таблицы `news`
--

INSERT INTO `news` (`id`, `title`, `content`, `date`, `user_id`) VALUES
(1, 'Nokia 6 (2018) может получить Snapdragon 660', 'Появились некоторые детали относительно обновленного варианта смартфона Nokia 6. Если верить утечкам, он будет интереснее оригинальной модели. Первый вариант Nokia 6 был анонсирован в феврале этого года. \r\nСамый мощный на тот момент смартфон HMD/Nokia получил чип Snapdragon 430, 3 + 32 ГБ и ценник в районе $250, что, по меркам 2017, очень печально. По имеющимся сведениям, Nokia 6 (2018) будет основан на производительном Snapdragon 660 и получит 4 ГБ RAM и 32 ГБ хранилища.\r\n\r\nЭто куда как более приемлемо. Ожидается, что новинка похвастается экраном 18:9 с тонкими рамками, следовательно, с вероятностью 99% сканер отпечатка пальца будет сзади. Представить Nokia 6 (2018) могут на Mobile World Congress, и наверняка модель будет дороже первого варианта, выпущенного почти год назад. ', '2017-12-11 11:12:35', 1);

-- --------------------------------------------------------

--
-- Структура таблицы `questions`
--

DROP TABLE IF EXISTS `questions`;
CREATE TABLE IF NOT EXISTS `questions` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `question` varchar(1000) COLLATE utf8_unicode_ci NOT NULL,
  `answer` varchar(1000) COLLATE utf8_unicode_ci NOT NULL,
  `variant1` varchar(1000) COLLATE utf8_unicode_ci NOT NULL,
  `variant2` varchar(1000) COLLATE utf8_unicode_ci NOT NULL,
  `variant3` varchar(1000) COLLATE utf8_unicode_ci NOT NULL,
  `test_id` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=19 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Дамп данных таблицы `questions`
--

INSERT INTO `questions` (`id`, `question`, `answer`, `variant1`, `variant2`, `variant3`, `test_id`) VALUES
(1, '<p class=\"MsoNormal\" style=\"margin-bottom: 10.0pt; text-align: justify; text-justify: inter-ideograph; text-indent: 28.4pt; line-height: 150%;\"><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 150%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Інформація - це&nbsp;</span></p>', 'усі відповіді вірні', 'знання, відомості, обізнаність', 'відомості про навколишній світ, його процеси та явища, які сприймаються людиною чи спеціальним пристроєм в різні способи за допомогою умовних сигналів, технічних засобів тощо', 'відомості про об\'єкти та явища навколишнього середовища, їх параметри, властивості і стан, які зменшують наявну ступінь невизначеності та неповноти знань', 1),
(2, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Як класифікують інформацію за формою представлення?</span></p>', 'Неперервна і дискретна', 'Повна і неповна', 'Електронна та письмова', 'Усі відповіді вірні', 1),
(3, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">До властивостей інформації не належить</span></p>', 'немає правильної відповіді', 'актуальність', 'цінність', 'повнота', 1),
(4, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Інформаційна технологія - це</span></p>', 'процес, що використовує сукупність засобів і методів збору, отримання, накопичення, зберігання, обробки, аналізу і передачі даних (первинної інформації) в організаційній структурі з використанням засобів обчислювальної техніки для отримання інформації нової якості про стан об\'єкта, процесу або явища', 'виробництво інформації для її аналізу та прийняття рішення для виконання певної дії.', 'процес, який визначається сукупністю засобів і методів обробки, виготовлення, зміни стану, властивостей, форми сировини або матеріалу', 'немає правильної відповіді', 1),
(5, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Що належить до основних принципів комп\'ютерної технології належить?</span></p>', 'всі відповіді вірні', 'Інтерактивний (діалоговий) режим роботи з комп\'ютером', 'Інтегрованість (взаємозв\'язок) з іншими програмними продуктами.', 'Гнучкість процесу зміни як даних, так і постановки задачі.', 1),
(6, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Модель - це</span></p>', 'всі відповіді вірні', 'об\'єкт-замінник об\'єкта-оригіналу, що забезпечує вивчення деяких властивостей останнього', 'спрощене подання системи для її аналізу і передбачення', 'спрощене подання системи для отримання якісних та кількісних результатів, необхідних для прийняття правильного управлінського рішення', 2),
(7, '<p class=\"MsoNormal\" style=\"margin-bottom: 10.0pt; text-align: justify; text-justify: inter-ideograph; text-indent: 28.4pt; line-height: 150%;\"><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 150%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Під терміном \"моделювання\" зазвичай розуміють</span></p>', 'процес створення точного опису системи; метод пізнання, що складається в створенні і дослідженні моделей', 'створення нових об\'єктів, які покращують характеристики попередніх', 'аналіз інформації про продажі товару', 'всі відповіді вірні', 2),
(8, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Як можна класифікувати моделі?</span></p>', 'Всі відповіді вірні', 'За строгістю опису', 'За ступенем фізичної реалізації (логічної незалежності)', 'За ступенем відображення динаміки процесів', 2),
(9, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Концептуальна (аналітична) модель системи призначена</span></p>', 'Подання більш детальної специфікації вимог до системи (більш точну, ніж у специфікаціях на етапі визначення вимог і розробки варіантів використання) і може розглядатися як перший крок до моделі проектування, тобто служити вхідними даними для проектування системи.', 'Подання системи з використанням мови розробників, що дозволяє вводити більше формалізму і може використовуватися для аналізу внутрішніх механізмів системи', 'Вирішення завдання аналізу, яке полягає в декомпозиції системи на більш дрібні елементи і визначення властивостей системи або середовища, що оточує систему', 'Всі відповіді вірні', 2),
(10, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">За строгістю опису моделі бувають</span></p>', 'Формальні та неформальні', 'Логічні та фізичні', 'Статичні та динамічні', 'Немає правильної відповіді', 2),
(11, '<p class=\"MsoNormal\" style=\"margin-bottom: 10.0pt; text-align: justify; text-justify: inter-ideograph; text-indent: 28.4pt; line-height: 150%;\"><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 150%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Лінгвістична змінна - це</span></p>', 'змінна, яка приймає значення з множини слів або словосполучень деякої природної, або штучної мови', 'математична величина, значення якої може змінюватись у межах певної задачі', 'А і Б правильні відповіді', 'Немає правильної відповіді', 3),
(12, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Що не належить до типів ІС?</span></p>', 'Немає правильної відповіді', 'інтелектуальні інформаційно-пошукові системи', 'розрахунково-логічні системи', 'експертні системи', 3),
(13, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Логіко-лінгвістичні методи опису систем основані на</span></p>', 'Всі відповіді вірні', 'поводження дослідної системи описується на звичайній (або близькій до звичайної) мові в термінах лінгвістичних змінних', 'Вхідні та вихідні параметри системи розглядаються як лінгвістичні змінні', 'якісний опис процесу задається сукупністю висловлювань ', 3),
(14, '<p class=\"MsoNormal\" style=\"margin-bottom: 10.0pt; text-align: justify; text-justify: inter-ideograph; text-indent: 28.4pt; line-height: 150%;\"><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 150%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Статистична інформація - це&nbsp;</span></p>', 'сукупність статистичних даних, які відображають соціально-економічні явища і процеси, формуються в процесі статистичного спостереження, а потім систематизуються, групуються, аналізуються та узагальнюються', 'виробництво інформації для її аналізу та прийняття рішення для виконання певної дії', 'спрощене подання системи для отримання якісних та кількісних результатів, необхідних для прийняття правильного управлінського рішення', 'Немає правильної відповіді', 4),
(15, '<p class=\"MsoNormal\" style=\"margin-bottom: 10.0pt; text-align: justify; text-justify: inter-ideograph; text-indent: 28.4pt; line-height: 150%;\"><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 150%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Статистичне спостереження - це</span></p>', 'Обидві відповіді правильні', 'планомірне, науково організоване збирання або одержання масових відомостей про явища і процеси суспільного життя', 'складна, трудомістка і відповідальна робота, яка залежно від масштабів дослідження виконується силами багатьох працівників', 'Немає правильної відповіді', 4),
(16, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Основні вимоги, що пред\'являються до статистичного спостереження</span></p>', 'Всі відповіді вірні', 'повнота статистичних даних', 'достовірність', 'одноманітність', 4),
(17, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Відповідно до поставлених цілей і завдань визначають</span></p>', 'Всі відповіді вірні', 'об\'єкт та одиницю спостереження', 'Одиниця обліку ', 'Час спостереження ', 4),
(18, '<p><span lang=\"UK\" style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: UK; mso-fareast-language: UK; mso-bidi-language: AR-SA;\">Ентропія - це</span></p>', 'логарифмічна міра невпорядкованості стану джерела повідомлень', 'хвороба', 'Повідомлення, що передаються людською мовою', 'Всі відповіді вірні', 5);

